import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig'; // Importe a instância configurada do axios
import Task from '../components/Task'; // Importe o componente Task
import TaskForm from '../components/TaskForm'; // Importe o componente TaskForm

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('/tasks');
        setTasks(response.data);
      } catch (err) {
        setError('Erro ao buscar tarefas: ' + (err.response?.data?.message || err.message));
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, []);

  const handleAddTask = async (task) => {
    try {
      const response = await axios.post('/tasks', task);
      setTasks((prevTasks) => [...prevTasks, response.data]);
    } catch (err) {
      setError('Erro ao adicionar tarefa: ' + (err.response?.data?.message || err.message));
    }
  };

  const handleUpdateTask = async (updatedTask) => {
    try {
      const response = await axios.put(`/tasks/${updatedTask.id}`, updatedTask);
      setTasks((prevTasks) =>
        prevTasks.map((t) => (t.id === response.data.id ? response.data : t))
      );
    } catch (err) {
      setError('Erro ao atualizar tarefa: ' + (err.response?.data?.message || err.message));
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`/tasks/${id}`);
      setTasks((prevTasks) => prevTasks.filter((t) => t.id !== id));
    } catch (err) {
      setError('Erro ao excluir tarefa: ' + (err.response?.data?.message || err.message));
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Tasks</h2>
      <TaskForm onAddTask={handleAddTask} />
      <ul>
        {tasks.map((task) => (
          <Task
            key={task.id}
            task={task}
            onUpdateTask={handleUpdateTask}
            onDeleteTask={handleDeleteTask}
          />
        ))}
      </ul>
    </div>
  );
};

export default Tasks;
