import React, { useState, useEffect } from 'react';
import '../styles/TasksForm.css'; // Certifique-se de que este caminho está correto

const TaskForm = ({ selectedTask, setSelectedTask, onAddTask, onUpdateTask }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [completed, setCompleted] = useState(false);

  useEffect(() => {
    if (selectedTask) {
      setTitle(selectedTask.title);
      setDescription(selectedTask.description);
      setCompleted(selectedTask.completed);
    } else {
      setTitle('');
      setDescription('');
      setCompleted(false);
    }
  }, [selectedTask]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const task = { title, description, completed };
    if (selectedTask) {
      onUpdateTask({ ...selectedTask, title, description, completed });
    } else {
      onAddTask(task);
    }
    setTitle('');
    setDescription('');
    setCompleted(false);
    setSelectedTask(null);
  };

  return (
    <div className="task-form-container"> {/* Adicione uma div com a classe task-form-container */}
      <h2>{selectedTask ? 'Atualizar Tarefa' : 'Adicionar Tarefa'}</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Título
          <input
            type="text"
            placeholder="Título"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </label>
        <label>
          Descrição
          <textarea
            placeholder="Descrição"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </label>
        <label>
          Concluída
          <input
            type="checkbox"
            checked={completed}
            onChange={(e) => setCompleted(e.target.checked)}
          />
        </label>
        <button type="submit">{selectedTask ? 'Atualizar' : 'Adicionar'} Tarefa</button>
      </form>
    </div>
  );
};

export default TaskForm;
