import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig'; // Importe a instância configurada do axios

const TaskForm = ({ onAddTask, taskToEdit, onUpdateTask }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [isEditMode, setIsEditMode] = useState(false);

  useEffect(() => {
    if (taskToEdit) {
      setTitle(taskToEdit.title || '');
      setDescription(taskToEdit.description || '');
      setIsEditMode(true);
    } else {
      setTitle('');
      setDescription('');
      setIsEditMode(false);
    }
  }, [taskToEdit]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isEditMode && taskToEdit) {
        const response = await axios.put(`/tasks/${taskToEdit.id}`, {
          title,
          description,
          completed: taskToEdit.completed
        });
        if (onUpdateTask) onUpdateTask(response.data);
      } else {
        const response = await axios.post('/tasks', {
          title,
          description,
          completed: false
        });
        if (onAddTask) onAddTask(response.data);
      }
      setTitle('');
      setDescription('');
    } catch (error) {
      console.error('Erro ao processar tarefa:', error);
      alert('Erro ao processar tarefa');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Título"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Descrição"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
      <button type="submit">{isEditMode ? 'Atualizar' : 'Adicionar'} Tarefa</button>
    </form>
  );
};

export default TaskForm;
