import React, { useState } from 'react';
import '../styles/t.css'; // Certifique-se de que este caminho está correto

const ContactForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    // Aqui você pode adicionar lógica para enviar os dados para um servidor ou API
    console.log({ name, email, subject, message });

    // Resetar o formulário após o envio
    setName('');
    setEmail('');
    setSubject('');
    setMessage('');
  };

  return (
    <div className="contact-form-container" role="form">
      <h1 className="contact-form-title">Formulário de Contato</h1>
      <form onSubmit={handleSubmit} className="contact-form">
        <div className="contact-form-group">
          <input
            type="text"
            name="name"
            className="contact-form-input"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            placeholder="Nome"
          />
          <label className="contact-form-label">Nome</label>
        </div>
        <div className="contact-form-group">
          <input
            type="email"
            name="email"
            className="contact-form-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="E-mail"
          />
          <label className="contact-form-label">E-mail</label>
        </div>
        <div className="contact-form-group">
          <input
            type="text"
            name="subject"
            className="contact-form-input"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            required
            placeholder="Assunto"
          />
          <label className="contact-form-label">Assunto</label>
        </div>
        <div className="contact-form-group">
          <textarea
            name="message"
            className="contact-form-textarea"
            rows="4"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
            placeholder="Mensagem"
          ></textarea>
          <label className="contact-form-label">Mensagem</label>
        </div>
        <button type="submit" className="contact-form-button">
          Enviar
        </button>
      </form>
    </div>
  );
};

export default ContactForm;
