import React, { useState, useEffect } from 'react';
import '../styles/TasksForm.css'; // Certifique-se de que este caminho está correto

const TaskForm = ({ selectedTask, setSelectedTask, onAddTask, onUpdateTask }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [completed, setCompleted] = useState(false);

  // UseEffect para carregar a tarefa selecionada
  useEffect(() => {
    if (selectedTask) {
      setTitle(selectedTask.title);
      setDescription(selectedTask.description);
      setCompleted(selectedTask.completed);
    } else {
      resetForm();
    }
  }, [selectedTask]);

  // Função para resetar o formulário
  const resetForm = () => {
    setTitle('');
    setDescription('');
    setCompleted(false);
    setSelectedTask(null);
  };

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    const task = { title, description, completed };

    if (selectedTask) {
      onUpdateTask({ ...selectedTask, ...task });
    } else {
      onAddTask(task);
    }

    resetForm();
  };

  return (
    <div className="task-form-container">
      <h2>{selectedTask ? 'Atualizar Tarefa' : 'Adicionar Tarefa'}</h2>
      <form onSubmit={handleSubmit} className="task-form">
        <label htmlFor="title" className="task-form-label">Título</label>
        <input
          id="title"
          type="text"
          className="task-form-input"
          placeholder="Título"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          aria-label="Título da Tarefa"
        />

        <label htmlFor="description" className="task-form-label">Descrição</label>
        <textarea
          id="description"
          className="task-form-textarea"
          placeholder="Descrição"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          aria-label="Descrição da Tarefa"
        />

        <div className="task-form-checkbox">
          <input
            id="completed"
            type="checkbox"
            checked={completed}
            onChange={(e) => setCompleted(e.target.checked)}
            aria-label="Tarefa Concluída"
          />
          <label htmlFor="completed">Concluída</label>
        </div>

        <button type="submit" className="task-form-button">
          {selectedTask ? 'Atualizar' : 'Adicionar'} Tarefa
        </button>
      </form>
    </div>
  );
};

export default TaskForm;
