// src/pages/Tasks.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';  // Certifique-se de que axios é importado
import Task from '../components/Task';
import TaskForm from '../components/TaskForm';
LoginForm.css

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/tasks', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        setTasks(response.data);
      } catch (error) {
        console.error('Erro ao buscar tarefas:', error);
      }
    };
    fetchTasks();
  }, []);

  const handleAddTask = async (task) => {
    try {
      const response = await axios.post('http://localhost:5000/api/tasks', task, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      setTasks([...tasks, response.data]);
    } catch (error) {
      console.error('Erro ao adicionar tarefa:', error);
    }
  };

  const handleUpdateTask = async (updatedTask) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/tasks/${updatedTask.id}`, updatedTask, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      setTasks(tasks.map(task => task.id === response.data.id ? response.data : task));
      setSelectedTask(null);
    } catch (error) {
      console.error('Erro ao atualizar tarefa:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      setTasks(tasks.filter(task => task.id !== id));
    } catch (error) {
      console.error('Erro ao excluir tarefa:', error);
    }
  };

  return (
    <div>
      <h2>Tasks</h2>
      <TaskForm 
        selectedTask={selectedTask} 
        setSelectedTask={setSelectedTask} 
        setTasks={setTasks}
        onAddTask={handleAddTask}
        onUpdateTask={handleUpdateTask}
      />
      <ul>
        {tasks.map(task => (
          <Task
            key={task.id}
            task={task}
            onUpdateTask={() => setSelectedTask(task)}
            onDeleteTask={handleDeleteTask}
          />
        ))}
      </ul>
    </div>
  );
};

export default Tasks;
